'use strict';

import React from 'react-native';

var APIs = require('./constants_api.js');
var moment = require('moment');

const {
  Component,
  StyleSheet,
  Text,
  View,
  TouchableOpacity,
  AlertIOS,
  Image
} = React;

var exports = module.exports = {};
exports.trimTime = function(timeStr) {
	timeStr = timeStr.replace("T", " ");
        timeStr = timeStr.substring(0, timeStr.length-5);
        return timeStr;
}
exports.encodeQueryData = function(params) {
        var ret = [];
        for (var d in params)
            ret.push(encodeURIComponent(d) + "=" + encodeURIComponent(params[d]));
        var params = ret.join("&");
        return params;
}
exports.createUrl = function(url, params) {
	var domain = "http://" + APIs.DOMAIN + ":" + APIs.PORT;

	var encodedParams = exports.encodeQueryData(params);
        var fullUrl = domain + url + "?" + encodedParams;
        return fullUrl;
}
exports.getToday = function () {
	var today = moment().format();
	var formated = moment(today).format('MMMM DD YYYY');	
	return formated;
}
exports.base64Icon = 'data:image/png;base64,';

